cq:listeners.afterinsert:
function(path, definition) { 
var val=CQ.Util.eval(CQ.HTTP.get(path+'.deleteLikeComp.html')); 
if(val[0].deleted=='true')
{
                CQ.Ext.Msg.show
                ({
                                'title':CQ.I18n.getMessage('Dialog Validation'),
                                'msg':CQ.I18n.getMessage('More than one like component is not allowed in a page.'),
                                'buttons':CQ.Ext.Msg.OK,
                                'icon':CQ.Ext.MessageBox.INFO,'scope':this
                }); 
                return false;
}
else {
                location.reload(true);
}}
