items.manageTabs:
function(tab,noSwitch){
var tabs=['general','tab1','tab2','tab3','tab4','tab5','tab6'];
var index=tabs.indexOf(tab);
if(index==-1) return;
if(index==1){
this.unhideTabStripItem(1);
this.hideTabStripItem(2);
this.hideTabStripItem(3);
this.hideTabStripItem(4);
this.hideTabStripItem(5);
this.hideTabStripItem(6);
}else if(index==2){
this.unhideTabStripItem(1);
this.unhideTabStripItem(2);
this.hideTabStripItem(3);
this.hideTabStripItem(4);
this.hideTabStripItem(5);
this.hideTabStripItem(6);
}else if(index==3){
this.unhideTabStripItem(1);
this.unhideTabStripItem(2);
this.unhideTabStripItem(3);
this.hideTabStripItem(4);
this.hideTabStripItem(5);
this.hideTabStripItem(6);
}else if(index==4){
this.unhideTabStripItem(1);
this.unhideTabStripItem(2);
this.unhideTabStripItem(3);
this.unhideTabStripItem(4);
this.hideTabStripItem(5);
this.hideTabStripItem(6);
}else if(index==5){
this.unhideTabStripItem(1);
this.unhideTabStripItem(2);
this.unhideTabStripItem(3);
this.unhideTabStripItem(4);
this.unhideTabStripItem(5);
this.hideTabStripItem(6);
}else if(index==6){
this.unhideTabStripItem(1);
this.unhideTabStripItem(2);
this.unhideTabStripItem(3);
this.unhideTabStripItem(4);
this.unhideTabStripItem(5);
this.unhideTabStripItem(6);
}else{
this.hideTabStripItem(1);
this.hideTabStripItem(2);
this.hideTabStripItem(3);
this.hideTabStripItem(4);
this.hideTabStripItem(5);
this.hideTabStripItem(6);
}
this.doLayout();
if(!noSwitch)this.activate(0);
}

items.manageType1:
function(type){
if(type=="pci1"){
this.findById("pci1").enable();
this.findById("pci1").expand();
this.findById("manual1").disable();
this.findById("manual1").collapse();
this.doLayout();
}else if(type="manual1"){
this.findById("pci1").disable();
this.findById("pci1").collapse();
this.findById("manual1").enable();
this.findById("manual1").expand();
this.findById("regional1").setValue("");
this.doLayout();
}else{
this.findById("pci1").disable();
this.findById("pci1").collapse();
this.findById("manual1").disable();
this.findById("manual1").collapse();
this.doLayout();
}
}

