preview.handler:
function(){
	var dialog = this.findParentByType('dialog');
	var trendingItems = new Array();
	var item = new Array();
	var query = "";
	trendingItems = dialog.getField('./trendingItem').getValue(); 
	var separator = "";
	var trendingItemValues = "";
	for(i=0;i<trendingItems.length;i++){
		trendingItemValues += separator;
		trendingItemValues += trendingItems[i];
		if(separator == "") separator = "^";
	}
	var displayOption  = dialog.getField('./displayOption').getValue();
	var headingText = dialog.getField('./heading').getValue();
	var textColor = dialog.getField('./textColor').getValue();
	var hoverColor = dialog.getField('./hoverColor').getValue();
	if(trendingItems.length>0){
		var url = dialog.path;			
		url = url + ".trendingPreview.html?displayoption="+displayOption+"&headingtext="+escape(headingText)+"&textcolor="+textColor+"&hovercolor="+hoverColor+"&trendingitems="+escape(trendingItemValues);	
		window.open(url);
	}
}

listeners.beforeSubmit:
function(dialog)
{
	var multiValues = new Array();
	var singleValue = new Array();
	var pagehandle = '';
	var pagehandle1 = '';
	var link = '';
	var groupsList = '';
	var difference = 'false';
	var check = '';

	pageHandle = location.toString();	
	var url2 = pageHandle.substring(pageHandle.lastIndexOf('/') + 1);        	
	var url1 = pageHandle.substring(0,pageHandle.lastIndexOf('/'));
	var temp = new Array();
	temp = url2.split('.');

	multiValues = dialog.getField('./trendingItem').getValue();

	for(var i = 0;i < multiValues.length; i++)
	{	
		singleValue = multiValues[i].split("|");		
		link = singleValue[2];
		groupsList = singleValue[3];

		if(groupsList.length == 0)
		{
			CQ.Ext.Msg.show(
			{	
				"title":"Allowed Access validation",
				"msg": "Please select a value in Allowed Access for trending item "+(i+1),
				"buttons":CQ.Ext.Msg.OK,
				"icon":CQ.Ext.MessageBox.INFO,
				"scope":this
			});
			return false;			
		}

		pageHandle1 = url1 + '/' +  temp[0] + '.checkGroupsValue.html?groups=' + groupsList;	
		check = CQ.Util.formatData(CQ.HTTP.eval(CQ.HTTP.get(pageHandle1)))[0].check;

		if(check)
		{
			CQ.Ext.Msg.show(
			{	
				"title":"Allowed Access validation",
				"msg": "Please select a value in Allowed Access for trending item "+(i+1),
				"buttons":CQ.Ext.Msg.OK,
				"icon":CQ.Ext.MessageBox.INFO,
				"scope":this
			});
			return false;				
		}

		if(groupsList.length != 0)
		{
			pageHandle = url1 + '/' +  temp[0] + '.checkGroups.html?pagURL=' + link + '&groups=' + groupsList;				
			difference = CQ.Util.formatData(CQ.HTTP.eval(CQ.HTTP.get(pageHandle)))[0].diff;
			if(!difference)
			{
				CQ.Ext.Msg.show(
				{	"title":"Allowed Access validation",
					"msg": "Groups selected don't match with CUG of the page given in trending item "+(i+1),
					"buttons":CQ.Ext.Msg.OK,
					"icon":CQ.Ext.MessageBox.INFO,
					"scope":this
				});

				return false;
				break;
			}
		}
	}

	var displayOption = dialog.getField('./displayOption').getValue().toString();

	if(displayOption == 'textcloud'){
		for(var i = 0;i < multiValues.length; i++){
			var trendingItemValues = multiValues[i].split("|");
			var fontSize = trendingItemValues[1].trim();
			var temp = i + 1;
			if(fontSize == ''){
				CQ.Ext.Msg.show(
				{	"title":"Font Size Validation",
					"msg": "Please provide font size for trending item " + temp,
					"buttons":CQ.Ext.Msg.OK,
					"icon":CQ.Ext.MessageBox.INFO,
					"scope":this
				});
				return false;
			}

		}
	}
}
